input = ""

# Part 1
def four(line):
    for i in range(len(line)-4):
        packet = line[i:i+4]

        if len(set(packet)) == 4:
            return i+4

print(four(input))

# Part 2
def fourteen(line):
    for i in range(len(line)-14):
        packet = line[i:i+14]

        if len(set(packet)) == 14:
            return i+14

print(fourteen(input))